mapper.select:
SELECT MENU_NO, MENU_DT, MENU_KIND, MENU_FILE_NAME
FROM MENU
WHERE MENU_NO = #{menu_number}

mapper.insert:
INSERT	INTO	MENU	(
						MENU_DT
						, MENU_KIND
						, MENU_FILE_NAME
					)
VALUES				(
						#{menu_date}
						, #{menu_kind}
						, "temp"
					)

INSERT	INTO	FOOD	(
						FOOD_NAME
						, FOOD_CAL
						, FOOD_KIND
					)
VALUES				(
						#{food_name}
						, #{food_calory}
						, #{food_kind}
					) 
ON DUPLICATE KEY UPDATE FOOD_NAME=#{food_name}, FOOD_CAL=#{food_calory}, FOOD_KIND=#{food_kind}

INSERT	INTO	FOOD_SET	(
						MENU_NO
						, FOOD_NO
					)
VALUES				(
						#{menu_no}
						, #{food_no}
					)

mapper.select:
SELECT FOOD_NO
FROM FOOD
WHERE FOOD_NAME = #{food_name}
